/**
 * Products Section Component
 * Displays featured product catalog in a horizontal row
 */
const ProductsSection = ({ products }) => {
  const scrollRef = React.useRef(null);
  const [activeIndex, setActiveIndex] = React.useState(0);
  
  // Function to scroll to the next product
  const scrollToNext = () => {
    if (activeIndex < products.length - 1) {
      setActiveIndex(activeIndex + 1);
    } else {
      // Loop back to the beginning
      setActiveIndex(0);
    }
  };
  
  // Function to scroll to the previous product
  const scrollToPrev = () => {
    if (activeIndex > 0) {
      setActiveIndex(activeIndex - 1);
    } else {
      // Loop back to the end
      setActiveIndex(products.length - 1);
    }
  };
  
  // Scroll to the active product when activeIndex changes
  React.useEffect(() => {
    if (scrollRef.current) {
      const container = scrollRef.current;
      const productWidth = 280; // card width (256px) + gap (24px)
      container.scrollTo({
        left: activeIndex * productWidth,
        behavior: 'smooth'
      });
    }
  }, [activeIndex]);

  return (
    <div id="products" className="py-16 px-4 md:px-8 bg-white">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-700 mb-4">Featured Products</h2>
        <p className="text-center text-gray-600 mb-8 max-w-2xl mx-auto">
          Our products are developed by healthcare professionals using premium quality ingredients.
        </p>
        
        <div className="relative">
          {/* Navigation arrows */}
          <button 
            onClick={scrollToPrev}
            className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-4 bg-white p-2 rounded-full shadow-md z-10 hover:bg-gray-100 transition-colors"
            aria-label="Previous product"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          
          <div 
            ref={scrollRef}
            className="flex overflow-x-hidden gap-6 pb-4 pt-2 px-2 scroll-smooth"
          >
            {products.map((product, index) => (
              <ProductCard 
                key={`product-${product.id}-${index}`} 
                product={product} 
                isActive={index === activeIndex}
              />
            ))}
          </div>
          
          <button 
            onClick={scrollToNext}
            className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-4 bg-white p-2 rounded-full shadow-md z-10 hover:bg-gray-100 transition-colors"
            aria-label="Next product"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
        
        {/* Product indicators */}
        <div className="flex justify-center mt-6 gap-2">
          {products.map((_, index) => (
            <button
              key={`indicator-${index}`}
              onClick={() => setActiveIndex(index)}
              className={`h-3 w-3 rounded-full transition-colors ${
                index === activeIndex ? 'bg-green-600' : 'bg-gray-300'
              }`}
              aria-label={`Go to product ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

